mapper.select:
SELECT * FROM user_session
WHERE user_id = #{userId} AND session_type = #{sessionType}
ORDER BY updated_time DESC

SELECT * FROM user_session
WHERE user_id = #{userId}
ORDER BY updated_time DESC

mapper.update:
UPDATE user_session
SET title = #{title}, updated_time = NOW()
WHERE chat_id = #{chatId}

mapper.select:
SELECT us.*, u.username, u.nickname
FROM user_session us
LEFT JOIN user u ON us.user_id = u.id


        AND us.user_id = #{userId}


        AND us.session_type = #{sessionType}


        AND us.title LIKE CONCAT('%', #{title}, '%')


        AND us.created_time > #{startTime}


        AND us.created_time < #{endTime}


ORDER BY us.updated_time DESC
